    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
       new addOrder().setVisible(true);
    }//GEN-LAST:event_addActionPerformed

    private void shipActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_shipActionPerformed
        if(Siparis.orders.isEmpty()){
            JOptionPane.showMessageDialog(null, "There is no order","Error",JOptionPane.ERROR_MESSAGE);
        }
        else{
           Siparis.money += Siparis.ordersPrices.get(0);
           Siparis.deleteData(Siparis.orders.get(0));
           Siparis.ordersPrices.remove(0);
           Siparis.orders.remove(0);
           money.setText(Siparis.money+"$");
        }
    }//GEN-LAST:event_shipActionPerformed

