    private void refreshActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_refreshActionPerformed
        orders.setText("");
        String s = "";
        for(int i = 0;i<Siparis.orders.size();i++){
            s+=Siparis.orders.get(i)+" <-> Price :"+Siparis.ordersPrices.get(i)+"\n";
        }
        orders.setText(s);
    }//GEN-LAST:event_refreshActionPerformed

    private void editActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_editActionPerformed
        if(Siparis.orders.isEmpty()){
            JOptionPane.showMessageDialog(null, "There is no order","Error",JOptionPane.ERROR_MESSAGE);
        }
        else{
            new editOrder().setVisible(true);
        }
    }//GEN-LAST:event_editActionPerformed

